                className='card'
                style={{ width: '30%', marginBottom: '20px' }}>
                <div className='card-body'>
                    <h3>{post.title}</h3>
                </div>
            </div>
        )
    })

    return (
        <div>
            <form onSubmit={onSubmit}>
                <div className='form-group'>
                    <label>Title</label>
                    <input
                        value={title}
                        onChange={e => setTitle(e.target.value)}
                        className='form-control'
                    />
                </div>
                <button className='btn btn-primary'>
                    Submit
                </button>
            </form>
        </div>
    )
}
